const express = require('express');
const http = require('http');
const socketIo = require('socket.io');
const connectDB = require('./config/db');
const User = require('./models/User');
const passport = require('passport');

// Connect Database
connectDB();

const app = express();
const server = http.createServer(app);
const io = socketIo(server, {
    cors: {
        origin: '*',
        methods: ['GET', 'POST']
    }
});

// Middleware
app.use(express.json()); // [OLD CODE] bodyParser.json() replacement
require('./config/auth')(passport);
app.use(passport.initialize());
const authRoutes = require('./routes/auth');
app.use('/api/users', authRoutes);

// Include setup function for socket event listeners
require('./sockets')(io);

const PORT = process.env.PORT || 3001;

app.get('/api/ping', (req, res) => {
  res.json({ message: 'pong' });
});

server.listen(PORT, () => {
    console.log(`Server running on port ${PORT}`);
});

import React, { useEffect } from 'react';
import { socket } from '../utils/socket';

const LiveStream = () => {
    useEffect(() => {
        navigator.mediaDevices.getUserMedia({ video: true }).then(stream => {
            const videoElement = document.querySelector('#videoStream');
            videoElement.srcObject = stream;

            socket.emit('join-stream', 'global-room'); // INPUT_REQUIRED {Specify the correct room name or dynamic room logic}

            // INPUT_REQUIRED {Implement WebRTC logic for sharing and receiving stream between peers}
        });

        return () => {
            socket.off('stream');
        };
    }, []);

    return (
        <div>
            <video id="videoStream" autoPlay playsInline></video>
            {/* INPUT_REQUIRED {Additional UI elements or controls related to live streaming} */}
        </div>
    );
};

export default LiveStream;

module.exports = (io, socket) => {
    socket.on('join-stream', (room) => {
        socket.join(room);
    });

    // INPUT_REQUIRED {Enhance the mechanism to broadcast/emit streams using appropriate WebRTC or similar streaming technology}

    socket.on('stream', (data) => {
        // Broadcasting to other clients in the 'room', except the sender
        socket.to(data.room).emit('stream', data.stream);
        // Be aware that raw media streams cannot be directly transmitted via Socket.IO
        // You'll need to implement streaming through a service or WebRTC data channels
    });
}
