const AstroChart = require('@astrodraw/astrochart');

exports.getAstrologyChart = (req, res) => {
  try {
    const { birthDate, birthTime, latitude, longitude } = req.body;
    const chartParameters = {
      date: birthDate,
      time: birthTime,
      latitude,
      longitude
      // INPUT_REQUIRED {Please adjust parameters here based on user input and AstroChart requirements}
    };

    const astrologyChart = new AstroChart(chartParameters);
    const result = astrologyChart.draw();

    res.json(result);
  } catch (err) {
    console.error(err);
    res.status(500).send('Server error');
  }
};
