const mongoose = require('mongoose');

const UserSchema = new mongoose.Schema({
  username: {
    type: String,
    required: true,
    unique: true
  },
  email: {
    type: String,
    required: true,
    unique: true
  },
  password: {
    type: String,
    required: true
  },
  birthDate: {
    type: Date,
    required: false
  },
  birthTime: {
    type: String,
    required: false
  },
  birthLatitude: {
    type: Number,
    required: false
  },
  birthLongitude: {
    type: Number,
    required: false
  },
  created_at: {
    type: Date,
    default: Date.now
  },
  last_login: {
    type: Date
  }
});

module.exports = mongoose.model('User', UserSchema);

const User = require('../models/User');

exports.updateProfile = async (req, res) => {
  try {
    const {
      birthDate,
      birthTime,
      birthLatitude,
      birthLongitude
    } = req.body;
    const user = await User.findById(req.user.id); 

    if (!user) {
      return res.status(404).json({ msg: 'User not found' });
    }

    user.birthDate = birthDate;
    user.birthTime = birthTime;
    user.birthLatitude = birthLatitude;
    user.birthLongitude = birthLongitude;

    await user.save();
    res.json(user);
  } catch (err) {
    console.error(err);
    res.status(500).send('Server Error');
  }
};

