const AstroChart = require('@astrodraw/astrochart');
const User = require('../models/User');

exports.getAstrologyChart = async (req, res) => {
  try {
    const user = await User.findById(req.user.id);
    if (!user) {
      return res.status(404).json({ msg: 'User not found' });
    }
    const chartParameters = {
      year: user.birthDate.getFullYear(),
      month: user.birthDate.getMonth() + 1,
      date: user.birthDate.getDate(),
      hour: parseInt(user.birthTime.split(':')[0], 10),
      minute: parseInt(user.birthTime.split(':')[1], 10),
      lat: user.birthLatitude,
      lng: user.birthLongitude
    };

    const astrologyChart = new AstroChart(chartParameters);
    const svgChart = astrologyChart.drawChart();
    res.set('Content-Type', 'image/svg+xml');
    res.send(svgChart);
  } catch (err) {
    console.error(err);
    res.status(500).send('Server Error');
  }
};
