import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Treasures = () => {
    const [products, setProducts] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch products from the backend
        const fetchProducts = async () => {
            try {
                const response = await axios.get('http://localhost:3001/api/products');
                console.log(response.data);
                setProducts(response.data);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };
        fetchProducts();
    }, []);

    const handleAddToCart = (productId) => {
        // Invoke addToCart logic here
        navigate('/cart');
    };

    return (
        <Container>
            <Row className="justify-content-md-center mt-5">
                <Col md={8}>
                    <h1>Astrology Treasures</h1>
                    <p>Discover a curated selection of astrology books.</p>
                </Col>
            </Row>
            <Row>
                {products.map(product => (
                    <Col md={4} key={product._id}>
                        <Card>
                            <Card.Img variant="top" src={product.imageUrl || "/images/eBook1.jpg"} />
                            <Card.Body>
                                <Card.Title>{product.title}</Card.Title>
                                <Card.Text>{product.description}</Card.Text>
                                <Button variant="primary" onClick={() => handleAddToCart(product._id)}>Add to Cart</Button>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>
        </Container>
    );
};

export default Treasures;
